import React, { useState } from "react";
import BlackStrip from "../Components/blackStrip";
import NavBar from "../Components/navbar";
import Footer from "../Components/footer";
import EquityTaxInfo from "./equityTaxInfo";
import { InputRadio } from "../Components/formInput";

export default function EquityTaxCal() {
  const [stats, setStats] = useState({
    sellYear: "",
    holdingPeriod: "",
  });

  function handleInput(event) {
    const { name, value } = event.target;
    return setStats((prevValue) => {
      return {
        ...prevValue,
        [name]: value,
      };
    });
  }

  return (
    <div>
      <BlackStrip bgcolor="#1e1b1b" height="2.2em" />
      <NavBar />
      <div className="sipContainer">
      <div class="heading">
          <h4 class="calHeading">LTCG Calculator â€“ Long Term Capital Gains Calculator</h4>
          <h6 class="calTagline">Find how much tax you need to pay on Shares</h6>
        </div>
        <br /><br />
        <div className="formQuestion">
          When did you sell the Equity Shares or Units
        </div>

        <InputRadio
          name="sellYear"
          label="Before 31st March 2018"
          value="before"
          onClick={handleInput}
        />
        <InputRadio
          name="sellYear"
          label="After 1st April 2018"
          value="after"
          onClick={handleInput}
        />
        <br />

        <div className="formQuestion">
          Holding Period (No of Years Between date of Purchase and sale)
        </div>

        <InputRadio
          name="holdingPeriod"
          label="Less than 1 year"
          value="less"
          onClick={handleInput}
        />
        <InputRadio
          name="holdingPeriod"
          label="1 year or more"
          value="more"
          onClick={handleInput}
        />


      </div>
      <br />
      <br />
      <br />
      <br />
      <EquityTaxInfo />
      <Footer />
    </div>
  );
}
