import React, { useState } from "react";
import "../css/sipCalculator.css";
import NavBar from "../Components/navbar";
import BlackStrip from "../Components/blackStrip";
import { InputNumberLeft } from "../Components/formInput";
import { incomeDetails } from "../dataset/incomeSection2";

export default function IncomeTaxCal(){
    const [stats,setStats]=useState({
        incomeSalary:0,
        exemptAllowance:0,
        interestIncome:0,
        interestHomeLoanSelfOccupied:0,
        incomeRental:0,
        interestHomeLoanLetOut:0,
        incomeDigitalAssets:0,
        incomeOthers:0,
        deduc80C:0,
        deduc80TTB:0,
        deduc80D:0,
        deduc80G:0,
        deduc80E:0,
        deduc80EEA:0,
        deduc80CCD:0,
    });
    function handleInput(event) {
        const { name, value } = event.target;
        return setStats((prevValue) => {
          return {
            ...prevValue,
            [name]: value,
          };
        });
      }

      function Section2(){
        return (
            <div>
                {incomeDetails.map((data)=>(
                    <InputNumberLeft
                    label={data.label}
                    id={data.name}
                    name={data.name}
                    unit="₹"
                    onInput={handleInput}
                    min="0"
                    value={stats.incomeSalary}
                  />
                ))}
                {/* <InputNumberLeft
                  label="Income from Salary"
                  id="customRange1"
                  name="incomeSalary"
                  unit="₹"
                  onInput={handleInput}
                  min="0"
                  value={stats.incomeSalary}
                /> */}
            </div>
        );
    }


    return(
        <div>
            <BlackStrip bgcolor="#1e1b1b" height="2.2em" />
            <NavBar />
            <div className="sipContainer">
                <div class="heading">
                    <h4 class="calHeading">Income Tax Calculator</h4>
                    <h6 class="calTagline">Find how much tax I need to pay</h6>
                </div>
                <br />
                <br />
                <InputNumberLeft
                  label="Income from Salary"
                  id="customRange1"
                  name="incomeSalary"
                  unit="₹"
                  onInput={handleInput}
                  min="0"
                  value={stats.incomeSalary}
                />
                {incomeDetails.map((data)=>(
                    <InputNumberLeft
                    label={data.label}
                    id={data.name}
                    name={data.name}
                    unit="₹"
                    onInput={handleInput}
                    min="0"
                    value={stats.incomeSalary}
                  />
                ))}
                <Section2 />
            </div>
        </div>
    );


}
