  }else{
    result="No tax to be paid as there is no gain"
  }
//   Short Term Capital Gain of ₹69,94,200 is chargeable to tax @ 15% i.e ₹10,49,130


  return (
    <div>
      <BlackStrip bgcolor="#1e1b1b" height="2.2em" />
      <NavBar />
      <div className="sipContainer">
      <div class="heading">
          <h4 class="calHeading">LTCG Calculator – Long Term Capital Gains Calculator</h4>
          <h6 class="calTagline">Find how much tax you need to pay on Shares</h6>
        </div>
        <br /><br />
        <div className="formQuestion">
          When did you sell the Equity Shares or Units
        </div>

        <InputRadio
          name="sellYear"
          label="Before 31st March 2018"
          value="before"
          onClick={handleInput}
        />
        <InputRadio
          name="sellYear"
          label="After 1st April 2018"
          value="after"
          onClick={handleInput}
        />
        <br />

        <div className="formQuestion">
          Holding Period (No of Years Between date of Purchase and sale)
        </div>

        <InputRadio
          name="holdingPeriod"
          label="Less than 1 year"
          value="less"
          onClick={handleInput}
        />
        <InputRadio
          name="holdingPeriod"
          label="1 year or more"
          value="more"
          onClick={handleInput}
        />

        <br />
        <div class="col-sm-4">
        <InputNumberLeft
              label="Sale Value"
              id="salePrice"
              name="salePrice"
              unit="₹"
              onInput={handleInput}
              min="0"
              value={stats.salePrice}
            />
        <InputNumberLeft
              label="Purchase Value"
              id="purchasePrice"
              name="purchasePrice"
              unit="₹"
              onInput={handleInput}
              min="0"
              value={stats.purchasePrice}
            />
        </div>
        <div>{result}</div>
      </div>
      <br />
      <br />
      <br />
      <br />
      <EquityTaxInfo />
      <Footer />
    </div>
  );
}
